import React, {useState, useEffect} from 'react';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';

const API_KEY = 'a8a674c4bbac72b278f44504cc780490';
const API_URL = `http://api.openweathermap.org/data/2.5/onecall?`;

function App() {

  const [lat, setLat] = useState({});
  const [lon, setLon] = useState({});
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLon(position.coords.longitude);
    });

    fetch(`${API_URL}lat=${lat}&lon=${lon}&exclude=hourly,minutley&units=metric&appid=${API_KEY}`)
    .then((res) => res.json())
    .then((data) => {
      setData(data);
      setLoading(false)
      console.log(data)
    });

  }, [lat, lon]);

  if(loading) return <h1>Loading...</h1>

  return (
    <section className="app">
      <LeftSide />
      {(data !== 'undefined') ? (<RightSide weatherData={data}/>) : (<div></div>)}
    </section>
  )
}

export default App;
