import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';

const API_KEY = 'cae54b8bd4ab32e6e35a832728873700';
const API_URL = `http://api.openweathermap.org/data/2.5/onecall?`;

function App() {

  // Toggle Class
  const [isToggle, setIsToggle] = useState(false)
  const toggle = (e) => {
    setIsToggle(!isToggle);
  }

  /* All Needed States */
  const [lat, setLat] = useState([]);
  const [lon, setLon] = useState([]);
  const [currentTemp, setCurrentTemp] = useState([]);
  const [desc, setDesc] = useState([]);
  const [currentIcon, setCurrentIcon] = useState([]);
  const [timeZone, setTimeZone] = useState([]);
  const [uvi, setUvi] = useState([]);
  const [windSpeed, setWindSpeed] = useState([]);
  const [sunrise, setSunrise] = useState([]);
  const [sunset, setSunset] = useState([]);
  const [humidity, setHumidity] = useState([]);
  const [visibility, setVisibility] = useState([]);
  const [windDeg, setWindDeg] = useState([]);
  const [rain, setRain] = useState([]);
  const [dayForecast, setDayForecast] = useState([]);
  /* All Needed States */

  useEffect(() => {
    // Get current position with Latitude & Longitude
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLon(position.coords.longitude);
    })

    // Request & get data from the API URL
    axios.get(`${API_URL}lat=${lat}&lon=${lon}&exclude=hourly,minutley&units=metric&appid=${API_KEY}`)
    .then((res) => {
      const getData = res.data;
      /* All Needed API Data */
      setDayForecast(getData.daily)
      setUvi(getData.daily[0].uvi);
      setWindSpeed(getData.daily[0].wind_speed);
      setSunrise(new Date(getData.daily[0].sunrise * 1000).toLocaleTimeString());
      setSunset(new Date(getData.daily[0].sunset * 1000).toLocaleTimeString());
      setHumidity(getData.daily[0].humidity);
      setVisibility(getData.current.visibility);
      setWindDeg(getData.current.wind_deg);
      setCurrentTemp(getData.current.temp);
      setDesc(getData.daily[0].weather[0].description);
      setCurrentIcon(getData.daily[0].weather[0].icon);
      setTimeZone(getData.timezone);
      setRain(getData.daily[0].rain);
      /* All Needed API Data */
    });
  }, [lat, lon])

  return (
    <section className="app">
      <LeftSide 
        currentTemp={currentTemp}
        desc={desc}
        currentIcon={currentIcon}
        toggle={toggle}
        isToggle={isToggle}
        timeZone={timeZone}
        rain={rain}
      />

      <RightSide 
        dayForecast={dayForecast}
        uvi={uvi}
        windSpeed={windSpeed}
        sunrise={sunrise}
        sunset={sunset}
        humidity={humidity}
        visibility={visibility}
        windDeg={windDeg}
        toggle={toggle}
        isToggle={isToggle}
      />
    </section>
  )
}

export default App;
