import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faCloud, faCrosshairs } from "@fortawesome/free-solid-svg-icons";
import "./LeftSide.css";
import './SortIcon';
import './SortIcon.css';
import city from "./city/cairotower.jpg";
import SortIcon from './SortIcon';

const LeftSide = (props) => {
    return (
        <div className="left_side">
            <div className="container">
                {/* Search Field -> Search for places */}
                <form className="search_field">
                    <div className="search_icon"><FontAwesomeIcon icon={faSearch}/></div>
                    <input type="search" name="search" placeholder="Search for places..."></input>
                    <button className="crosshairs_icon"><FontAwesomeIcon icon={faCrosshairs}/></button>
                </form>
                {/* Search Field -> Search for places */}

                {/* API Icon */}
                <div className="API_icon">
                    <img src={`http://openweathermap.org/img/wn/${props.currentIcon}@4x.png`} alt="placeholder"/>
                </div>
                {/* API Icon */}

                {/* Current Weather */}
                <div className="current_weather">
                    <h1 className="temp">{`${props.currentTemp}°C`}</h1>
                    <h5 className="day_time">Monday, <span>16:00</span></h5>
                    <hr></hr>
                </div>
                <div className="current_weather2">
                        <div className="cloud"> <FontAwesomeIcon icon={faCloud}/> <span>{props.desc}</span></div>
                        <div className="rain">
                            <SortIcon />        
                            <span>{`Rain - ${30}%`}</span>
                        </div>
                        <div className="city">
                            <img src={city} alt="Cairo"/>
                        </div>
                    </div>
                {/* Current Weather */}
            </div>
        </div>
    )
}

export default LeftSide;
