import React, { Component } from 'react';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';


/* 
* All Needed Data *

1- Image Icon
2- Temprature
3- Description

*/


const API_KEY = 'a8a674c4bbac72b278f44504cc780490';

class App extends Component {

  state = {
    imageIcon: '',
    temp: '',
    description: '',
    error: ''
  }

  getWeather = async (e) => {
    e.preventDefault();
    const search = e.target.elements.search.value;
    const API_URL = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${search},${search}&units=metric&appid=${API_KEY}`);
    const data = await API_URL.json();

    console.log(data)

    if(search) {
      this.setState({
        imageIcon: data.weather[0].icon,
        temp: data.main.temp,
        description: data.weather[0].description,
        error: ''
      })
    } else {
      this.setState({
        imageIcon: '',
        temp: '',
        description: '',
        error: ''
      })
    }
  }



  render() {
    return (
      <section className="app">
        <LeftSide 
        getWeather={this.getWeather}
        imageIcon= {this.state.imageIcon}
        temp= {this.state.temp}
        description= {this.state.description}
        />
        <RightSide />
      </section>
    )
  }
}

export default App;
