import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';

const API_KEY = 'eabd8b1db297d680fe7d9ebdbb9b028a';
const API_URL = `http://api.openweathermap.org/data/2.5/onecall?`;
const weekdays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

function App() {
    const [state, setState] = useState(false)
    const toggle = (e) => {
        setState(!state);
    }

  // All Needed Data
  const [lat, setLat] = useState([]);
  const [lon, setLon] = useState([]);

  /* 7 Days Forecast */
  const [day0, setDay0] = useState([]);
  const [day1, setDay1] = useState([]);
  const [day2, setDay2] = useState([]);
  const [day3, setDay3] = useState([]);
  const [day4, setDay4] = useState([]);
  const [day5, setDay5] = useState([]);
  const [day6, setDay6] = useState([]);
  const [day7, setDay7] = useState([]);

  const [icon0, setIcon0] = useState([]);
  const [icon1, setIcon1] = useState([]);
  const [icon2, setIcon2] = useState([]);
  const [icon3, setIcon3] = useState([]);
  const [icon4, setIcon4] = useState([]);
  const [icon5, setIcon5] = useState([]);
  const [icon6, setIcon6] = useState([]);
  const [icon7, setIcon7] = useState([]);

  const [temp0, setTemp0] = useState([]);
  const [temp1, setTemp1] = useState([]);
  const [temp2, setTemp2] = useState([]);
  const [temp3, setTemp3] = useState([]);
  const [temp4, setTemp4] = useState([]);
  const [temp5, setTemp5] = useState([]);
  const [temp6, setTemp6] = useState([]);
  const [temp7, setTemp7] = useState([]);
  /* 7 Days Forecast */

  const [currentTemp, setCurrentTemp] = useState([]);
  const [desc, setDesc] = useState([]);
  const [currentIcon, setCurrentIcon] = useState([]);
  // const [rain, setRain] = useState([]);
  const [uvi, setUvi] = useState([]);
  const [windSpeed, setWindSpeed] = useState([]);
  const [sunrise, setSunrise] = useState([]);
  const [sunset, setSunset] = useState([]);
  const [humidity, setHumidity] = useState([]);
  const [visibility, setVisibility] = useState([]);
  const [windDeg, setWindDeg] = useState([]);


  useEffect(() => {
    // Get current position with Latitude & Longitude
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLon(position.coords.longitude);
    })

    // Request & get data from the API URL
    axios.get(`${API_URL}lat=${lat}&lon=${lon}&exclude=hourly,minutley&units=metric&appid=${API_KEY}`)
    .then((res) => {
      const getData = res.data;

      /* 7 Days Forecast */
      setDay0(weekdays[new Date(getData.daily[0].dt * 1000).getDay()]);
      setDay1(weekdays[new Date(getData.daily[1].dt * 1000).getDay()]);
      setDay2(weekdays[new Date(getData.daily[2].dt * 1000).getDay()]);
      setDay3(weekdays[new Date(getData.daily[3].dt * 1000).getDay()]);
      setDay4(weekdays[new Date(getData.daily[4].dt * 1000).getDay()]);
      setDay5(weekdays[new Date(getData.daily[5].dt * 1000).getDay()]);
      setDay6(weekdays[new Date(getData.daily[6].dt * 1000).getDay()]);
      setDay7(weekdays[new Date(getData.daily[7].dt * 1000).getDay()]);

      setIcon0(getData.daily[0].weather[0].icon);
      setIcon1(getData.daily[1].weather[0].icon);
      setIcon2(getData.daily[2].weather[0].icon);
      setIcon3(getData.daily[3].weather[0].icon);
      setIcon4(getData.daily[4].weather[0].icon);
      setIcon5(getData.daily[5].weather[0].icon);
      setIcon6(getData.daily[6].weather[0].icon);
      setIcon7(getData.daily[7].weather[0].icon);

      setTemp0(getData.daily[0].temp.day);
      setTemp1(getData.daily[1].temp.day);
      setTemp2(getData.daily[2].temp.day);
      setTemp3(getData.daily[3].temp.day);
      setTemp4(getData.daily[4].temp.day);
      setTemp5(getData.daily[5].temp.day);
      setTemp6(getData.daily[6].temp.day);
      setTemp7(getData.daily[7].temp.day);
      /* 7 Days Forecast */

      setUvi(getData.current.uvi);
      setWindSpeed(getData.current.wind_speed);
      setSunrise(new Date(getData.current.sunrise * 1000).toLocaleTimeString());
      setSunset(new Date(getData.current.sunset * 1000).toLocaleTimeString());
      setHumidity(getData.current.humidity);
      setVisibility(getData.current.visibility);
      setWindDeg(getData.current.wind_deg);
      setCurrentTemp(getData.current.temp);
      setDesc(getData.current.weather[0].description);
      setCurrentIcon(getData.current.weather[0].icon);
      // setRain(getData.current.rain);
    });

  }, [lat, lon])

  return (
    <section className="app">
      <LeftSide 
        currentTemp={currentTemp}
        desc={desc}
        currentIcon={currentIcon}
        toggle={toggle}
        state={state}
        // rain={rain}
      />
      <RightSide 
      /* 7 Days Forecast */

        day0={day0}
        day1={day1}
        day2={day2}
        day3={day3}
        day4={day4}
        day5={day5}
        day6={day6}
        day7={day7}

        temp0={temp0}
        temp1={temp1}
        temp2={temp2}
        temp3={temp3}
        temp4={temp4}
        temp5={temp5}
        temp6={temp6}
        temp7={temp7}

        icon0={icon0}
        icon1={icon1}
        icon2={icon2}
        icon3={icon3}
        icon4={icon4}
        icon5={icon5}
        icon6={icon6}
        icon7={icon7}
      /* 7 Days Forecast */
        uvi={uvi}
        windSpeed={windSpeed}
        sunrise={sunrise}
        sunset={sunset}
        humidity={humidity}
        visibility={visibility}
        windDeg={windDeg}
        toggle={toggle}
        state={state}
      />
    </section>
  )
}

export default App;
