import React, { Component } from 'react';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';


/* 
* All Needed Data *

1- Image Icon
2- Temprature
3- Description

*/


// const API_KEY = 'a8a674c4bbac72b278f44504cc780490';

class App extends Component {

  state = {
    latitude: null,
    longitude: null
  }

  position = async() => {
    navigator.geolocation.getCurrentPosition(this.position, this.setState({
      latitude: this.position.coords.latitude,
      longitude: this.position.coords.longitude
    }))
  }

  getWeather = async (e) => {
    e.preventDefault();

    // const API_URL = await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=hourly,daily&units=metric&appid=${API_KEY}`);
    // const data = await API_URL.json();

    // console.log(data)
  }



  render() {
    return (
      <section className="app">
        <LeftSide 
        getWeather={this.getWeather}
        />
        <RightSide />
      </section>
    )
  }
}

export default App;
