/* eslint-disable react/jsx-no-duplicate-props */
import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faCrosshairs } from "@fortawesome/free-solid-svg-icons";
import moment from 'moment';
import "./LeftSide.css";
import SortIcon from'./SortIcon';

const LeftSide = (props) => {
    return (
        <div className={(props.state ? 'left_side dark_mode_left' : 'left_side')}>
            <div className="container">
                {/* Get Current Position */}
                <form className="get_position">
                    <div className="search_icon"><FontAwesomeIcon icon={faSearch}/></div>
                    <div className='get_position_output'>{props.timeZone.toUpperCase()}</div>
                    <button className="crosshairs_icon"><FontAwesomeIcon icon={faCrosshairs}/></button>
                </form>
                {/* Get Current Position */}

                {/* API Icon */}
                <div className="API_icon">
                    <img src={`http://openweathermap.org/img/wn/${props.currentIcon}@4x.png`} alt="icon"/>
                </div>
                {/* API Icon */}

                {/* Current Weather */}
                <div className="current_weather">
                    <h1 className="temp">{`${Math.round(props.currentTemp)}°C`}</h1>
                    <h5 className="day_time">{moment().format('dddd')}, <span>{moment().format('h:mm:ss a')}</span></h5>
                    <hr></hr>
                </div>
                <div className="current_weather2">
                        <div className="cloud"> <img src={`http://openweathermap.org/img/wn/${props.currentIcon}.png`} alt="icon"/> <span>{props.desc}</span></div>
                        <div className="rain">
                            <SortIcon />        
                            <span>{`Rain - ${30}%`}</span>
                        </div>
                        <div className="city">
                            <img src={`images/cairo.jpg`} alt="Cairo"/>
                        </div>
                    </div>
                {/* Current Weather */}
            </div>
        </div>
    )
}

export default LeftSide;
