import React, { Component } from 'react';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';



const API_KEY = 'a8a674c4bbac72b278f44504cc780490';
// const ICON_URL = 'https://openweathermap.org/img/w';

class App extends Component {

  getWeather = async (e) => {
    const city = e.target.elements.search.value;
    const API_URL = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},eg&units=metric&appid=${API_KEY}`);
    const data = await API_URL.json();

    console.log(data.city)
  }



  render() {
    return (
      <section className="app">
        <LeftSide getWeather={this.getWeather}/>
        <RightSide />
      </section>
    )
  }
}

export default App;
