import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import LeftSide from './components/LeftSide';
import RightSide from './components/RightSide';

const API_KEY = 'eabd8b1db297d680fe7d9ebdbb9b028a';
const API_URL = `http://api.openweathermap.org/data/2.5/onecall?`;

function App() {

  // All Needed Data
  const [lat, setLat] = useState(null);
  const [lon, setLon] = useState(null);
  const [temp0, setTemp0] = useState(null);
  const [temp1, setTemp1] = useState(null);
  const [temp2, setTemp2] = useState(null);
  const [temp3, setTemp3] = useState(null);
  const [temp4, setTemp4] = useState(null);
  const [temp5, setTemp5] = useState(null);
  const [temp6, setTemp6] = useState(null);
  const [temp7, setTemp7] = useState(null);
  const [icon, setIcon] = useState(null);
  const [uvi, setUvi] = useState(null);
  const [windSpeed, setWindSpeed] = useState(null);
  const [sunrise, setSunrise] = useState(null);
  const [sunset, setSunset] = useState(null);
  const [humidity, setHumidity] = useState(null);
  const [visibility, setVisibility] = useState(null);
  const [windDeg, setWindDeg] = useState(null);


  useEffect(() => {
    // Get current position with Latitude & Longitude
    navigator.geolocation.getCurrentPosition((position) => {
      setLat(position.coords.latitude);
      setLon(position.coords.longitude);
    })

    // Request & get data from the API URL
    axios.get(`${API_URL}lat=${lat}&lon=${lon}&exclude=hourly,minutley&units=metric&appid=${API_KEY}`)
    .then((getWeather) => {

      setTemp0(getWeather.data.daily[0].temp.day);
      setTemp1(getWeather.data.daily[1].temp.day);
      setTemp2(getWeather.data.daily[2].temp.day);
      setTemp3(getWeather.data.daily[3].temp.day);
      setTemp4(getWeather.data.daily[4].temp.day);
      setTemp5(getWeather.data.daily[5].temp.day);
      setTemp6(getWeather.data.daily[6].temp.day);
      setTemp7(getWeather.data.daily[7].temp.day);
      // setIcon(getWeather.data.daily.weather.icon);
      setUvi(getWeather.data.current.uvi);
      setWindSpeed(getWeather.data.current.wind_speed);
      setSunrise(getWeather.data.current.sunrise);
      setSunset(getWeather.data.current.sunset);
      setHumidity(getWeather.data.current.humidity);
      setVisibility(getWeather.data.current.visibility);
      setWindDeg(getWeather.data.current.wind_deg);
    });

  }, [lat, lon])

  return (
    <section className="app">
      <LeftSide />
      <RightSide 
        temp0={temp0}
        temp1={temp1}
        temp2={temp2}
        temp3={temp3}
        temp4={temp4}
        temp5={temp5}
        temp6={temp6}
        temp7={temp7}
        icon={icon}
        uvi={uvi}
        windSpeed={windSpeed}
        sunrise={sunrise}
        sunset={sunset}
        humidity={humidity}
        visibility={visibility}
        windDeg={windDeg}
      />
    </section>
  )
}

export default App;
